def return_card(name, attack, health, tech_level, card_text, race, flavor_text, hero_id):
    result={}
    result['name']=name
    result['attack'] = attack
    result['health'] = health
    result['tech_level'] = tech_level
    result['card_text'] = card_text
    result['race'] = race
    result['flavor_text'] = flavor_text
    result['hero_id'] = hero_id
    return result



