def return_hero(name, health, hp_name, hp_text, hp_cost, hp_id):
    result={}
    result['name']=name
    result['health'] = health
    result['hero_power_name'] = hp_name
    result['hero_power_text'] = hp_text
    result['hero_power_cost'] = hp_cost
    result['hero_power_id'] = hp_id
    return result

